mapper.select:
select state, apply_date, r.id as "resume.id", r.u_id as "resume.user.id", r.name as "resume.name", j.id as "job.id", j.name as "job.name", c.company_name as "job.post_company.company_name" from tb_resume r, tb_job j, tb_apply a, tb_company c where 1 and r.id=a.r_id and j.id=a.j_id and j.post_company=c.id
and r.u_id=#{uId}
and j.id=#{jId}
order by apply_date

select state, apply_date, r.id as "resume.id", r.u_id as "resume.user.id", r.name as "resume.name", r.grade as "resume.grade", j.id as "job.id", j.name as "job.name" from tb_resume r, tb_job j, tb_apply a where 1 and r.id=a.r_id and j.id=a.j_id
and j.post_company=#{id}
order by apply_date
limit ${start},${count}

mapper.insert:
insert into
tb_apply(
r_id,
j_id,
state
)values(
#{resume.id},
#{job.id},
#{state}
)
